mapper.select:
         select * 
         from tbl_wx_phonenumber
         where  1=1

           and wechatid = #{info.wechatid} 


           and phonenumber = #{info.phonenumber} 



		       and DATE_FORMAT(create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{info.begin_time}, '%Y-%m-%d')   


		      and DATE_FORMAT(create_time, '%Y-%m-%d') <= DATE_FORMAT(#{info.end_time}, '%Y-%m-%d')    


          order by create_time desc  


				LIMIT #{page.limit}


				OFFSET #{page.offset}

         select count(*) 
         from tbl_wx_phonenumber
         where  1=1

           and  state=#{info.state}


           and  task_result=#{info.task_result}


           and wechatid = #{info.wechatid} 


           and phonenumber = #{info.phonenumber} 



		       and DATE_FORMAT(create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{info.begin_time}, '%Y-%m-%d')   


		      and DATE_FORMAT(create_time, '%Y-%m-%d') <= DATE_FORMAT(#{info.end_time}, '%Y-%m-%d')

select *
from tbl_wx_phonenumber 
where  state='1'  and  wechatid = #{wechatid}  limit #{count}

select *
from tbl_wx_phonenumber 
where  state='1' and wechatid is null limit #{count}
